current.custom_css:
:root {
  --input-rounded: 5px;
}
body .m-menu {
  --main-link-py: 28px;
}
body .m-button.m-button--link:before,
body .m-button.m-button--link:after {
  height: 1px;
}

.m-product-card .m-product-card__media {
  border-radius: 5px;
}
.m-product-card .m-product-card__title {
  font-size: 24px;
  font-weight: 400;
}
.m-product-card__price .m-price {
  font-weight: 600;
}
.m-product-card__price .m-price__sale .m-price-item--sale {
  color: #b22234;
}
.m-header__search.m-search-form--full {
  --color-border: transparent;

  min-width: 287px;
  background-color: #f5f5f5;
  flex-direction: row-reverse;
  border-radius: 5px;
}

.m-footer--block-newsletter {
  --color-border: transparent;
}
.m-footer--block-newsletter .m-newsletter-form--bordered .m-newsletter-form__input,
.m-footer--block-newsletter .m-newsletter-form--bordered .m-newsletter-form__button {
  background-color: #f5f5f5;
}
.m-footer--block-newsletter .m-newsletter-form--bordered .m-newsletter-form__button {
  color: #fff;
  background-color: #000;
}

.m-footer--block-newsletter .m-footer--block-newsletter-desc {
  color: #696969;
}
body .m-footer--block .m-footer--block-title {
  font-size: 18px;
}

@media (min-width: 1536px) {
  .m-header__search.m-search-form--full {
    min-width: 390px;
  }
}
@media (min-width: 768px) {
  body .m-footer--block .m-footer--block-title {
    font-size: 24px;
  }
  body .m-footer--block-newsletter .m-footer--block-title {
    font-size: 34px;
  }
  body .m-footer--block-newsletter .m-footer--block-inner {
    max-width: 85%;
  }
  body .m-footer .m-newsletter-form {
    margin-top: 50px;
  }
  body .m-footer--bottom .social-media-links {
    margin-bottom: 0;
  }
}
